import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor() {
    super()
    this.state ={
      loading: false,
      character: {}
    }
  }

  componentDidMount() {
    this.setState({loading: true})
    fetch("https://swapi.co/api")
  }

  render() {
    return (
      <div>
        {this.state.loading ? "Loading..." : this.state.character.name}
      </div>
    );
  };

}

export default App;
