import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor() {
    super()
    this.state ={
      firstName: ""
    }
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange () {
    this.setState({firstName: event.target.value})
  }

  render() {

    return (
      <form>
        <input type = "text" 
        placeholder = "first name" 
        onChange={this.handleChange}/>
        <h2>{this.state}</h2>
      </form>
    );
  };

}

export default App;
