import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 0,
        address: {
            street: 'Kamil Mohd Odeh'
        }
    }
    formatCount = () => {
        const { count } = this.state;
        if (count === 0) {
            return 'Zero'
        }

    }
    render() { 
        return (
            <React.Fragment>
                <span>{this.formatCount}</span>
                <button>Increment</button>
            </React.Fragment>
        );
    }
}

export default Counter;
