import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 0,
        // tags: ['tag1', 'tag2', 'tag3']
        // address: {
        //     street: 'Kamil Mohd Odeh'
        // },
        // imageUrl: 'https://picsum.photos/200'
    };

    // constructor() {
    //     super();
    //     this.handleIncrement = this.handleIncrement.bind(this); //binding method
    // }

    // styles = {
    //     fontSize: 50,
    //     fontWeight: 'bold'
    // }; use (style={this.styles}) with it

    // or use style={{ fontSize: 30 }} for inline style

    // renderTags() {
    //     if (this.state.tags.length === 0) return <p>There are no tags!</p>;
    //     return <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul>
    // }
    handleIncrement = (product) => {  // or use arrow function for binding 
        console.log(product);
        this.setState({count: this.state.count+1});
    }

    doHandleIncrement = () => {
        this.handleIncrement({id: 1})
    }

    render () { 
        return (
            <React.Fragment>
                {/* <img src={this.state.imageUrl} alt=""></img> */}
                <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
                <button onClick={this.doHandleIncrement} className="btn btn-secondary btn-sm">Increment</button>
                {/* {this.state.tags.length === 0 && 'Please create a new tag!'}
                {this.renderTags()} */}
            </React.Fragment>
        );
    }

    formatCount () {
        const { count } = this.state;
        return count === 0 ? 'Zero' : count;
    };

    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === 0) ? "warning" : "primary";
        return classes;
    }
}

export default Counter;
