import React, {Component} from 'react';

class App extends Component {

  state = {
    hours: 2,
    minutes: 0,
    seconds: 0
  }

  handleTimer() {
    let seconds = this.state.seconds;
    let minutes = this.state.minutes;
    let hours = this.state.hours;

    if (hours ===0 && minutes === 0 && seconds === 0) {
      this.setState({hours})
    } else {
      if (seconds === 0) {
        seconds = 59;
        if (minutes === 0) {
          minutes = 59;
          if (hours !== 0) {
            hours--;
          }
        } else {
          minutes --;
        }
      } else {
        seconds--;
      }
    }
  }

  render() { 
    return (
      <div className="timer">
        <div className="count-down border bg-white">
          <h4>{setTimeout(this.handleTimer, 1000)}</h4>
        </div>
      </div>
    );
  }

}

export default App;
