import React, {Component} from 'react';
import CommentCard from './components/CommentCard'



class App extends Component {

  state = {
    name: "Ahmad Al-Ghzawi",
    imgUrl: "/home/owner/Desktop/GHZAWI/PHOTO/Less Size/DSC_8641.jpg",

  }


  render () {
    return (
      <div className="container">
        <CommentCard name={this.state.name} imgUrl={this.state.imgUrl}/>

        <input type = "text" />
        <button>Comment</button>
      </div>
    )
  }

}

export default App;
