import React, {Component} from 'react';
import CommentCard from './components/CommentCard';


class App extends Component {

  state = {
    name: "Ahmad Al-Ghzawi",
    nickName: "GHZ",

    comments : [
      {id: 1, text: "Hi there!"}
    ]
  }

  getTime () {

  }

  getComment = () => {
    console.log(this.state.comments);
    let comments = this.state.comments;
    let text = document.getElementById('text').val();
    console.log(text);
    let id = this.state.comments.length + 1;
    comments.push({id, text});
    this.setState({comments});
  }

  render () {
    return (
      <div className="container">
        <CommentCard name={this.state.name} nickName={this.state.nickName} imgUrl={this.state.imgUrl}/>

        <input ref={text => this.comment = text} className="mt-5" type = "text"/>
        <button onClick={this.getComment}>Comment</button>
      </div>
    )
  }

}

export default App;
