import React from 'react';
import './App.css';
import Header from './components/Header'
import Main from './components/Main'
import Footer from './components/Footer'

// const App = () => <h1>Hello there!</h1>

function App() {
  const firstName = "Ahmad";
  const lastName = "Ghzawi";
  const date = new Date(2019, 8, 9, 1);
  const hours = date.getHours();
  let timeOfDay;
  const styles = {
    fontWeight: "bold",
    fontSize: 35 // when using pixels or percentage, use strings
  }

  if (hours < 12) {
    timeOfDay = "morning";
    styles.color = "yellow";
  } else if (hours >= 12 && hours < 17) {
    timeOfDay = "afternoon";
    styles.color = "darkgray";
  } else if (hours >= 17 && hours < 20) {
    timeOfDay = "evening";
    styles.color = "red"
  } else {
    timeOfDay = "night";
    styles.color = "black"
  };

  // const styles = {
  //   color: "yellow",
  //   backgroundColor: "purple",
  //   fontWeight: "bold",
  //   fontSize: 44 // when using pixels or percentage, use strings
  // }

  return (
    <React.Fragment>
      {/* <p>{firstName} {lastName}</p> */}
      {/* <p>{firstName+" "+lastName}</p> */}
      <p>{`${firstName} ${lastName}`}</p>
      <p>{hours}</p>
      <h3 style= {styles}>Good {timeOfDay}!</h3> 
      {/* or use style= {{color: "yellow", backgroundColor: "purple"}} for inline styling */}
      <Header />
      <Main />
      <Footer />
    </React.Fragment>
  );

  // return (
  //   <div>
  //     <input type="checkbox" />
  //     <p>Placeholder text here</p>

  //     <input type="checkbox" />
  //     <p>Placeholder text here</p>

  //     <input type="checkbox" />
  //     <p>Placeholder text here</p>

  //     <input type="checkbox" />
  //     <p>Placeholder text here</p>
  //   </div>
  // );
}


export default App;
